<script setup lang="ts">
// Generated by GitHub Copilot
import { onMounted, ref } from "vue";
import { useRouter } from "vue-router";
import { api } from "./api";
import { getGoogleAuthUrl } from "./utils/config";
import { getAuthStatus } from "./lib/auth";

interface User {
  id: string;
  name: string;
  email: string;
  avatarUrl?: string;
}

// State
const user = ref<User | null>(null);
const isLoading = ref(true);
const dropdownOpen = ref(false);
const subscriptionRequired = ref(false);

// Get router
const router = useRouter();

// Toggle dropdown menu
const toggleDropdown = () => {
  dropdownOpen.value = !dropdownOpen.value;
};

// Close dropdown when clicking outside
const closeDropdownOnClickOutside = (event: MouseEvent) => {
  const userMenu = document.querySelector(".user-menu");
  if (
    userMenu &&
    !userMenu.contains(event.target as Node) &&
    dropdownOpen.value
  ) {
    dropdownOpen.value = false;
  }
};

// Add event listener to close dropdown on clicks outside the menu
onMounted(() => {
  document.addEventListener("click", closeDropdownOnClickOutside);
});

// Check if user is logged in
const checkAuth = async () => {
  try {
    const status = await getAuthStatus();
    if (status.authed && !status.subscriptionRequired) {
      user.value = status.user;
    } else if (status.subscriptionRequired) {
      user.value = status.user;
      subscriptionRequired.value = true;
      router.push("/subscribe");
    } else {
      user.value = null;
    }
  } catch (error) {
    console.error("Authentication check failed:", error);
    user.value = null;
  } finally {
    isLoading.value = false;
  }
};

// Handle logout
const logout = async () => {
  try {
    await api.post("/auth/logout");
    user.value = null;
    // Redirect to home page and refresh the page to clear any cached data
    router.push("/").then(() => {
      window.location.reload();
    });
  } catch (error) {
    console.error("Logout failed:", error);
  }
};

// Check auth status on component mount
onMounted(() => {
  checkAuth();
});
</script>

<template>
  <div class="app-container">
    <!-- Navigation bar -->
    <header class="navbar">
      <div class="container nav-container">
        <div class="nav-brand">
          <router-link to="/" class="nav-brand-link">
            <!-- Generated by GitHub Copilot -->
            <span class="brand-icon">üèÉ</span> ChoreSprint
          </router-link>
        </div>

        <nav class="nav-links" v-if="!isLoading">
          <div class="nav-links-container">
            <router-link to="/" class="nav-link" exact>Home</router-link>
            <router-link v-if="user" to="/dashboard" class="nav-link"
              >Dashboard</router-link
            >
          </div>

          <div v-if="user" class="user-menu">
            <div class="user-info" @click="toggleDropdown">
              <img
                :src="
                  user.avatarUrl ||
                  'https://ui-avatars.com/api/?name=' +
                    encodeURIComponent(user.name)
                "
                :alt="user.name"
                class="avatar"
              />
              <span class="user-name">{{ user.name }}</span>
            </div>
            <div
              class="dropdown-menu"
              :class="{ 'dropdown-menu-active': dropdownOpen }"
            >
              <router-link
                to="/manage-subscription"
                class="dropdown-item dropdown-manage-sub"
                @click="toggleDropdown"
              >
                Manage Subscription
              </router-link>
              <button class="dropdown-item" @click="logout">Logout</button>
            </div>
          </div>

          <a
            v-else
            :href="getGoogleAuthUrl()"
            class="btn btn-primary nav-sign-in"
            >Sign In</a
          >
        </nav>

        <!-- Loading placeholder -->
        <div v-else class="loader-inline"></div>
      </div>
    </header>

    <!-- Main content area -->
    <main class="main-content">
      <router-view @login-success="checkAuth" />
    </main>

    <!-- Footer -->
    <footer class="footer">
      <div class="container">
        <div class="footer-content">
          <div class="copyright">
            <!-- Generated by GitHub Copilot -->
            &copy; {{ new Date().getFullYear() }} ChoreSprint. All rights
            reserved.
          </div>
          <div class="footer-links">
            <a href="#" class="footer-link">Terms</a>
            <a href="#" class="footer-link">Privacy</a>
            <a href="#" class="footer-link">Help</a>
          </div>
        </div>
      </div>
    </footer>
  </div>
</template>

